const fetch = require('node-fetch');

module.exports = async (key, pl, user) => {
    const plataform = {
        'xbox': 1,
        'psn': 2,
        "pc": 5
    }[pl]

    return await fetch(`https://public-api.tracker.gg/apex/v1/standard/profile/${plataform}/${user}`, {
        headers: {
            'TRN-Api-Key': key
        }
    })
        .then(res => res.json())
        .then(json => json.data)
}